SELECT CAST(ROWNUM AS UNSIGNED) AS ROWNUM, SIZE, GUBUN, PROVISION_GUBUN, FILENAME, INSTALL_YN, VERSION_CODE, SEQ, COMPANY_SEQ, NAME, ACCFLAG, OSCODE, OSCODE2, OSTYPE, VERNUM, CATETYPE, SCOPE, REGDATE, KEYVAL, REG_SEQ, ICON_NAME, USE_YN, COMPLETE_YN, SCOPE_YN, DOWNYN, DOWNCNT, DOWNAMT, MEMDOWNSTDT, MEMDOWNENDT, COUPON_YN, COUPON_NUM, NONDOWNYN, NONDOWNCNT, NONDOWNAMT, NONMEMDOWNSTDT, NONMEMDOWNENDT, LIMIT_YN FROM ( SELECT @RNUM := @RNUM + 1 AS ROWNUM, SIZE, GUBUN, PROVISION_GUBUN, FILENAME, INSTALL_YN, VERSION_CODE, SEQ, COMPANY_SEQ, NAME, ACCFLAG, OSCODE, OSCODE2, OSTYPE, VERNUM, CATETYPE, SCOPE, REGDATE, KEYVAL, REG_SEQ, ICON_NAME, USE_YN, COMPLETE_YN, SCOPE_YN, DOWNYN, DOWNCNT, DOWNAMT, MEMDOWNSTDT, MEMDOWNENDT, COUPON_YN, COUPON_NUM, NONDOWNYN, NONDOWNCNT, NONDOWNAMT, NONMEMDOWNSTDT, NONMEMDOWNENDT, LIMIT_YN FROM ( SELECT SIZE , GUBUN, PROVISION_GUBUN, FILENAME, INSTALL_YN, VERSION_CODE, SEQ, COMPANY_SEQ, NAME, ACCFLAG, OSCODE, OSCODE2, OSTYPE, VERNUM, CATETYPE, SCOPE, REGDATE, KEYVAL, REG_SEQ, ICON_NAME, USE_YN, COMPLETE_YN, SCOPE_YN, DOWNYN, DOWNCNT, DOWNAMT, MEMDOWNSTDT, MEMDOWNENDT, COUPON_YN, COUPON_NUM, NONDOWNYN, NONDOWNCNT, NONDOWNAMT, NONMEMDOWNSTDT, NONMEMDOWNENDT, LIMIT_YN FROM ( SELECT app_size as SIZE,'APP' as GUBUN, provision_gb as PROVISION_GUBUN, file_name as FILENAME, install_gb as INSTALL_YN, VERSION_CODE, app_seq as SEQ, (SELECT company_seq FROM TB_MEMBER MEM WHERE MEM.user_seq = TB_APP.reg_user_seq) as COMPANY_SEQ, app_name as NAME, use_user_gb as ACCFLAG, CASE ostype WHEN '4' THEN '4' WHEN '1' THEN '5' WHEN '2' THEN '6' WHEN '3' THEN '7' END OSCODE, ostype as OSCODE2, CASE ostype WHEN '1' THEN 'ALL' WHEN '2' THEN 'IOS' WHEN '3' THEN 'IOS(PAD)' WHEN '4' THEN 'Android' ELSE '' END as OSTYPE, ver_num as VERNUM, reg_gb as CATETYPE, CASE distr_gb WHEN '1' THEN 'íì' WHEN '2' THEN 'ë¹íì' END as SCOPE, reg_dt as REGDATE, (SELECT bundle_name FROM TB_BUNDLE BUN WHERE BUN.app_seq = TB_APP.app_seq limit 1) as KEYVAL, reg_user_seq as REG_SEQ, icon_save_file as ICON_NAME, use_gb AS USE_YN, complet_gb as COMPLETE_YN, distr_gb as SCOPE_YN, mem_down_gb as DOWNYN, CAST(IFNULL(mem_down_cnt,0) as UNSIGNED) as DOWNCNT, CAST(IFNULL(mem_down_amt,0) as UNSIGNED) as DOWNAMT, DATE_FORMAT(mem_down_start_dt, '%Y-%m-%d') as MEMDOWNSTDT, DATE_FORMAT(mem_down_end_dt, '%Y-%m-%d') as MEMDOWNENDT, coupon_gb as COUPON_YN, coupon_num as COUPON_NUM, nonmem_down_gb as NONDOWNYN, CAST(IFNULL(nonmem_down_cnt,0) as UNSIGNED) as NONDOWNCNT, CAST(IFNULL(nonmem_down_amt,0) as UNSIGNED) as NONDOWNAMT, DATE_FORMAT(nonmem_down_star_dt, '%Y-%m-%d') as NONMEMDOWNSTDT, DATE_FORMAT(nonmem_down_end_dt, '%Y-%m-%d') as NONMEMDOWNENDT, limit_gb as LIMIT_YN FROM TB_APP WHERE 1 = 1 AND app_resultCode = '1' AND reg_user_seq IS NOT NULL UNION ALL SELECT contents_size as SIZE, 'CONTENTS' as GUBUN, 'CONTENTS' as PROVISION_GUBUN, upload_save_file as FILENAME, 'CONTENTS' as INSTALL_YN, 'CONTENTS' as VERSION_CODE, contents_seq as SEQ, (SELECT company_seq FROM TB_MEMBER MEM WHERE MEM.user_seq = TB_CONTENTS.reg_user_seq) as COMPANY_SEQ, contents_name as NAME, 'CONTENTS' as ACCFLAG, contents_type as OSCODE, contents_type as OSCODE2, CASE contents_type WHEN '1' THEN 'HTML5' WHEN '2' THEN 'ePub' WHEN '3' THEN 'PDF' ELSE '' END as OSTYPE, ver_num as VERNUM, '3' as CATETYPE, CASE distr_gb WHEN '1' THEN 'íì' WHEN '2' THEN 'ë¹íì' END as SCOPE, reg_dt as REGDATE, contents_url as KEYVAL, reg_user_seq as REG_SEQ, '' as ICON_NAME, use_gb AS USE_YN, complet_gb as COMPLETE_YN, distr_gb as SCOPE_YN, mem_down_gb as DOWNYN, CAST(IFNULL(mem_down_cnt,0) as UNSIGNED) as DOWNCNT, CAST(IFNULL(mem_down_amt,0) as UNSIGNED) as DOWNAMT, DATE_FORMAT(mem_down_start_dt, '%Y-%m-%d') as MEMDOWNSTDT, DATE_FORMAT(mem_down_end_dt, '%Y-%m-%d') as MEMDOWNENDT, coupon_gb as COUPON_YN, coupon_num as COUPON_NUM, nonmem_down_gb as NONDOWNYN, CAST(IFNULL(nonmem_down_cnt,0) as UNSIGNED) as NONDOWNCNT, CAST(IFNULL(nonmem_down_amt,0) as UNSIGNED) as NONDOWNAMT, DATE_FORMAT(nonmem_down_star_dt, '%Y-%m-%d') as NONMEMDOWNSTDT, DATE_FORMAT(nonmem_down_end_dt, '%Y-%m-%d') as NONMEMDOWNENDT, limit_gb as LIMIT_YN FROM TB_CONTENTS WHERE 1 = 1 AND reg_user_seq IS NOT NULL
AND contents_seq = 0
) A WHERE 1 = 1
AND COUPON_YN = '1' AND COUPON_NUM = #{coupon_Num}
ORDER BY REGDATE DESC ) B INNER JOIN (SELECT @RNUM := 0) C ) D
